import React, { Component } from "react";
import InputBox from "../Component/InputBox";
import InputList from "../Component/InputList";

class App extends Component {
  constructor() {
    super();
    this.state = {
      message: "",
      item: [],
    };
  }
  onHandleChange = (event) => {
    const setMessage = event.target.value;
    return setMessage;
  };

  onHandleClick = () => {
    this.setState({ message: this.onHandleChange });
  };

  addItemsToList = () => {
    this.setState({ item: this.message });
  };

  render() {
    return (
      <div>
        <InputBox
          onHanleChange={this.onHandleChange}
          onHandleClick={this.onHandleClick}
        />
        <InputList todoItem={this.addItemsToList} />
      </div>
    );
  }
}

export default App;
